// SPDX-License-Identifier: MIT
pragma solidity ^0.8.7;

contract DecentralizedInstagram {
  Post[] public posts;
  uint public nextPostId = 0;

  struct Post {
    uint id;
    string ipfsHash;
    string text;
    address user;
  }

  event PostCreated(uint id, string ipfsHash, string text, address user);

  function createPost(string memory ipfsHash, string memory text) public {
    uint idForNewPost = nextPostId;
    address user = msg.sender;

    Post memory newPost = Post(idForNewPost, ipfsHash, text, user);
    posts.push(newPost);

    nextPostId += 1;

    emit PostCreated(idForNewPost, ipfsHash, text, user);
  }

  function getPosts() public view returns (Post[] memory) {
    return posts;
  }
}
